import * as  React from "react"
import {Link} from "react-router-dom"
import "./PrintoutAccountStatement.scss"
import {
    Button,
    Col,
    Label,
    Row,
    InputGroupAddon,
    InputGroup
} from "reactstrap";
import {inject, observer} from "mobx-react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import {IOperationStore} from "../../stores/types";

interface OperationProps {
    operationStore?: IOperationStore
}

interface IState {
    startDate: any,
    endDate: any,
    printExecDate: any
}

@inject('operationStore')
@observer
class PrintoutAccountStatement extends React.Component<OperationProps, IState> {
    constructor(props: OperationProps) {
        super(props);
        this.state = {
            startDate: new Date(),
            endDate: new Date(),
            printExecDate: new Date(),
        };
    }

//function for updating the right date variable
    setDate = (date: any, variable: any) => {
        if (variable === "startDate") {
            ;
            this.setState(prevState => ({
                ...prevState,
                startDate: date,
            }))
        }
        if (variable === "endDate") {
            ;
            this.setState(prevState => ({
                ...prevState,
                endDate: date,
            }))
        }
        if (variable === "printExecDate") {
            ;
            this.setState(prevState => ({
                ...prevState,
                printExecDate: date,
            }))
        }
    }
//function for disabling the next button
    isDisabled = () => {
        if (this.state.startDate !== null && this.state.endDate !== null && this.state.printExecDate !== null) {
            return false
        } else {
            return true
        }
    }
    //function for passing data to operationstore
    submit = (evt: any) => {
        evt.preventDefault();
        const startDate = this.state.startDate.getFullYear() + '-' + (('0' + (this.state.startDate.getMonth() + 1)).slice(-2)) + '-' + ('0' + this.state.startDate.getDate()).slice(-2);
        const endDate = this.state.endDate.getFullYear() + '-' + (('0' + (this.state.endDate.getMonth() + 1)).slice(-2)) + '-' + ('0' + this.state.endDate.getDate()).slice(-2);
        const printExecDate = this.state.printExecDate.getFullYear() + '-' + (('0' + (this.state.printExecDate.getMonth() + 1)).slice(-2)) + '-' + ('0' + this.state.printExecDate.getDate()).slice(-2);
        const data = {
            "startDate": startDate,
            "endDate": endDate,
            "printExecutionDate": printExecDate,
        };
        this.props.operationStore!.setDefineAction(data)
    };

    render() {
        return (
            <div className="printout-account-statement-view">

                <Row>
                    <InputGroup className="printout-account-statement-view__startdate">
                        <Col className="printout-account-statement-view__input-label-container">
                            <Label>Turva-%</Label>
                        </Col>
                        <Col className="printout-account-statement-view__input-container">
  <input/>
                        </Col>
                    </InputGroup>

                    <InputGroup>
                        <Col className="printout-account-statement-view__input-label-container">
                            <Label>Säästöt</Label>
                        </Col>
                        <Col className="printout-account-statement-view__input-container">
<input/>
                        </Col>
                    </InputGroup>
                </Row>
                <Row>
                <InputGroup>
                    <Col className="printout-account-statement-view__input-label-container">
                        <Label>Eläkkeen aloitus ikä</Label>
                    </Col>
                    <Col className="printout-account-statement-view__input-container">
<input/>
                    </Col>
                </InputGroup>
                <InputGroup>
                    <Col className="printout-account-statement-view__input-label-container">
                        <Label>Eläkkeen lopetus ikä</Label>
                    </Col>
                    <Col className="printout-account-statement-view__input-container">
                        <input/>
                    </Col>
                </InputGroup>
                </Row>
                    <Button id={"nextPageButton"} disabled={this.isDisabled()} className={"printout-account-statement-view__nextbutton"}
                            onFocus={(evt: any) => this.submit(evt)}>Aloita eläke</Button>
            </div>
        )
    }
}


export default PrintoutAccountStatement;
