import {action, observable} from 'mobx';
interface IError {
    text: string,
    title: string
}
export default class ErrorStore {
    @observable public isErrorVisible = false;
    @observable public error: IError = {
        text: '',
        title: '',
    };

    // @ts-ignore
    private rootStore: any;

    constructor(rootStore: any) {
        this.rootStore = rootStore;
    }
    //function for opening error modal
    @action
    public showError = (errorText: string, errorTitle: string) => {

            this.error = {
                text: errorText,
                title: errorTitle,
            };
            this.isErrorVisible = true;

    };
    //function for closing error modal
    @action
    public closeError = () => {

            this.isErrorVisible = false;
            this.error = {
                text: '',
                title: '',
            };

    }

}
