import React, {useState, useRef, createRef, useEffect} from "react"
import "./AnnouncementReceiversTable.scss"
import {
    Button,
    Input,
    Row,
    DropdownToggle,
    DropdownMenu,
    DropdownItem,
    Table,
    ButtonDropdown
} from "reactstrap";
import {inject, observer} from "mobx-react";
import "react-datepicker/dist/react-datepicker.css";



const AnnouncementReceiversTable = inject('operationStore', 'printOutStore')(observer((props:any) => {

    const [dropdownOpen, setOpen] = useState(false);
    const [dropdownIndex, setIndex] = useState();
    const toggle = () => setOpen(!dropdownOpen);
    const [pendingReceiver, setPendingReceiver]=useState("");
    const [draggedItem, setDraggedItem] = useState({})

    const addNewTableColumn = () => {
        console.log(props.orders)
        props.updateOrders({order: props.orders.length + 1, receiver: ""},"newColumn")

    }

    const isOpen=(index:any)=>{
        if(index===dropdownIndex && dropdownOpen){
            return true
        }else{
            return false
        }
    };
    const addNewReceiver =(evt:any,index:number)=>{
        if(pendingReceiver !=="") {
            evt.preventDefault();
            props.updateOrders(props.orders.map((target: any, int: number) => {
                if (int === index) {
                    const newTarget = {
                        order: target.order,
                        receiver: pendingReceiver
                    }
                    return newTarget
                } else {
                    return target
                }
            }),"newReceiver")
        }else{
            return
        }
    };
    const removeNewReceiver =(evt:any,index:any)=>{
        setPendingReceiver("")
        props.updateOrders(props.orders.filter((target: any, int: number) => {
            if (int !== index) {
                return target
            }
        }),"Remove.svg")

    }
    useEffect(()=>{
        table()
console.log(dropdownOpen)
    },)
    const setPending =(evt:any)=>{

        evt.preventDefault();
        setPendingReceiver(evt.target.value)
    }
    const onDragStart = (e: any, index: any) => {
        setDraggedItem(props.orders[index])
        e.dataTransfer.effectAllowed = "move";
        e.dataTransfer.setData("text/html", e.target.parentNode);
        e.dataTransfer.setDragImage(e.target.parentNode, 20, 20);
    };

    const onDragOver = (index: any) => {
        const draggedOverItem = props.orders[index];
        // if the item is dragged over itself, ignore
        if (draggedItem === draggedOverItem) {
            return;
        }

        // filter out the currently dragged item
        let items: any = props.orders.filter((item:any) => item !== draggedItem);

        // add the dragged item after the dragged over item
        items.splice(index, 0, draggedItem);
        items.map((item:any,index:any)=>{
            item.order= index+1;
        })

        props.updateOrders(items);
    };

    const table =()=> {
        const variables = props.orders
        if(variables.length>0)
        return variables
    }

    return (
        <div className="announcement-receivers-table-view">

            <Table className="announcement-receivers-table-view__table">
                <thead className={"announcement-receivers-table-view__table-header"}>
                <tr>
                    <th></th>
                    <th>Order</th>
                    <th>Receiver</th>
                    <th></th>
                </tr>
                </thead>
                <tbody className={"announcement-receivers-table-view__table-body"}>
                {(table()!== null)
                    &&
                    table().map((target: any, index: number) => (
                    <tr key={index} onDragOver={() => onDragOver(index)}>
                        <td id={"dragButton"} draggable  onDragStart={e => onDragStart(e, index)}> </td>
                        <td draggable onDragStart={e => onDragStart(e, index)}>{target.order}</td>
                        {((props.orders[index].receiver === ""
                                &&
                                <td draggable onDragStart={e => onDragStart(e, index)}>
                                    <Row>
                                        <Input id={"receiverInput"} onChange={(evt)=>{setPending(evt)}} className={"announcement-receivers-table-view__table-body-input"} type="select" name="select">
                                            {props.printOutStore!.announcementReceivers.map((target: any, index: any) => (
                                                <option id={"receiverInputOption"+ index} value={target} key={index}>{target}</option>
                                            ))}
                                        </Input>
                                    </Row>
                                </td>)
                            ||
                            <td draggable
                                onDragStart={e => onDragStart(e, index)}>{target.receiver}</td>
                        )}
                        <td><ButtonDropdown isOpen={isOpen(index)} toggle={toggle}>
                            <DropdownToggle id={"receiverDropdownButton"} caret onClick={(index:any)=>setIndex(index)}>
                            </DropdownToggle>
                            <DropdownMenu>
                                <DropdownItem id={"removeNewReceiverTableColumn"} onClick={(evt:any)=>{removeNewReceiver(evt,index)}}>Remove</DropdownItem>
                            </DropdownMenu>
                        </ButtonDropdown>
                        </td>
                    </tr>

                ))}
                </tbody>
            </Table>
            <Button id={"addNewReceiverTableColumn"} onClick={() => addNewTableColumn()}>add</Button>
        </div>
    )
}))


export default AnnouncementReceiversTable;
